devices.map:
function(doc) {
  var clients = ['iphone','android','instagram','web','windows phone']
  if(doc.source)
  {
     for(var i=0;i<clients.length;i++)
     if(doc.source.toLowerCase().indexOf(clients[i]) > 0)
        emit(clients[i],1);
  }  
}

politics.map:
function(doc) {
   var positive = ["good","great","excellent","strong","positive"];
   var negative = ["bad","damn","disappoint","useless","negative"];
   var poiliticians = ["fuck"];    	

   if(doc.text){
     for(var i=0;i<positive.length;i++){
        var tweet = doc.text.toLowerCase(); 
        if(tweet.indexOf(positive[i]) > 0 && tweet.indexOf(poiliticians[0]) >0)
           emit(poiliticians[0]+ "+",1);         
     }

     for(var i=0;i<negative.length;i++){
        var tweet = doc.text.toLowerCase(); 
        if(tweet.indexOf(negative[i]) > 0 && tweet.indexOf(poiliticians[0]) >0)
           emit(poiliticians[0] + "-",1);         
     }
  }

}

